package Wipro.Training;

public class forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub\
		
		// Write the Whole Number from 1 to 50
		System.out.println("Whole Number From 1 to 50 are ");
		for(int i=0;i<=50;i++) {
			
			System.out.print(i + " ");
		}
		

	}

}
