package Wipro.Training;

public class ifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// age prediction of 18 year Student
		
		int age = 12;
		
		if(age < 18) {
			System.out.println("Student is less than 18");
			
		}
		else if(age == 18){
			System.out.println("Student found of age 18");
				
		}
		else {
			System.out.println("student is greater than 18");
			
		}
			
		

	}

}
