package Wipro.Training;

public class whileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Program to Display number from -10 to 10
		
		System.out.println("Number from -10 to 10");
		
		
		int i = -10;
		while(i<=10) {
			System.out.print(i + " ");
			i++;
		}
		

	}

}
